import React, { useEffect, useState } from "react";

function Cart() {
  const [cart, setCart] = useState(null);
  const [cartItems, setCartItems] = useState([]); // Added cartItems state

  useEffect(() => {
    fetch('https://fakestoreapi.com/carts/5')
      .then((res) => res.json())
      .then((data) => setCart(data))
      .catch((error) => console.error("Error fetching carts:", error));
  }, []);

  // Moved addItemToCart inside the Cart component
  function addItemToCart(item) {
    setCartItems([...cartItems, item]);
  }

  // Moved removeItemFromCart inside the Cart component
  function removeItemFromCart(index) {
    const newCartItems = cartItems.filter((_, i) => i !== index);
    setCartItems(newCartItems);
  }

  return (
    <div>
      <h1>Cart Component</h1>
      <ul>
        {cart && (
          <li key={cart.id}>
            <h2>Cart #{cart.id}</h2>
            <p>User ID: {cart.userId}</p>
            <p>Date: {cart.date}</p>
            <h3>Products:</h3>
            <ul>
              {cart.products.map((product) => (
                <li key={product.productId}>
                  Product ID: {product.productId}, Quantity: {product.quantity}
                </li>
              ))}
            </ul>
          </li>
        )}
      </ul>

      {/* Render products and add them to cart */}
      {productList.map((product) => (
        <div key={product.id}>
          <h3>{product.name}</h3>
          <p>Price: ${product.price}</p>
          <button onClick={() => addItemToCart(product)}>Add to Cart</button>
        </div>
      ))}

      {/* Shopping Cart */}
      <h2>Shopping Cart</h2>
      <ul>
        {cartItems.map((item, index) => (
          <li key={index}>
            {item.name} - ${item.price}
            <button onClick={() => removeItemFromCart(index)}>Remove</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

// Example product list
const productList = [
  { id: 1, name: 'Product 1', price: 10 },
  { id: 2, name: 'Product 2', price: 15 },
  // ...
];

export default Cart;










